import { useEffect, useState } from "react";
import { X } from "lucide-react";

export default function BookList({ onBorrow, onSelectBook }) {
  const [books, setBooks] = useState([]);
  const [selectedBooks, setSelectedBooks] = useState([]);

  // Pending filters (used before "Apply")
  const [pendingFilters, setPendingFilters] = useState({
    search: "",
    genre: "",
    availability: "",
    priceRange: [0, 1000],
    sortBy: ""
  });

  // Actual filters applied after "Apply Filters"
  const [filters, setFilters] = useState(pendingFilters);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/books")
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setBooks(data);
      });
  }, []);

  const handleSelectBook = (book) => {
    if (selectedBooks.find((b) => b.id === book.id)) {
      setSelectedBooks(selectedBooks.filter((b) => b.id !== book.id));
    } else {
      setSelectedBooks([...selectedBooks, book]);
    }
  };

  const removeFilter = (type) => {
    const updated = { ...filters };
    if (type === "genre") updated.genre = "";
    if (type === "availability") updated.availability = "";
    if (type === "price") updated.priceRange = [0, 1000];
    setPendingFilters(updated);
    setFilters(updated);
  };

  const applyFilters = () => {
    setFilters({ ...pendingFilters });
  };

  const filteredBooks = books
    .filter((book) =>
      book.title.toLowerCase().includes(filters.search.toLowerCase())
    )
    .filter((book) => (filters.genre ? book.genre === filters.genre : true))
    .filter((book) =>
      filters.availability === "true"
        ? book.available === true
        : filters.availability === "false"
        ? book.available === false
        : true
    )
    .filter(
      (book) =>
        book.price >= filters.priceRange[0] &&
        book.price <= filters.priceRange[1]
    );

  const sortedBooks = [...filteredBooks].sort((a, b) => {
    if (filters.sortBy === "title") return a.title.localeCompare(b.title);
    if (filters.sortBy === "priceLowHigh") return a.price - b.price;
    if (filters.sortBy === "priceHighLow") return b.price - a.price;
    if (filters.sortBy === "availability") return b.available - a.available;
    return 0;
  });

  const totalSelected = selectedBooks.length;
  const totalPrice = selectedBooks.reduce((sum, b) => sum + b.price, 0);

  return (
    <div>
      <div className="filter-section">
        <input
          className="filter-input"
          placeholder="Search by title..."
          value={pendingFilters.search}
          onChange={(e) =>
            setPendingFilters({ ...pendingFilters, search: e.target.value })
          }
        />
        <select
          className="filter-input"
          value={pendingFilters.genre}
          onChange={(e) =>
            setPendingFilters({ ...pendingFilters, genre: e.target.value })
          }
        >
          <option value="">All Genres</option>
          <option value="Fiction">Fiction</option>
          <option value="Science">Science</option>
          <option value="Finance">Finance</option>
          <option value="Self-help">Self-help</option>
          <option value="Philosophy">Philosophy</option>
          <option value="Biography">Biography</option>
          <option value="Fantasy">Fantasy</option>
          <option value="Romance">Romance</option>
          <option value="Business">Business</option>
          <option value="History">History</option>
        </select>
        <select
          className="filter-input"
          value={pendingFilters.availability}
          onChange={(e) =>
            setPendingFilters({
              ...pendingFilters,
              availability: e.target.value
            })
          }
        >
          <option value="">All</option>
          <option value="true">Available</option>
          <option value="false">Borrowed</option>
        </select>
        <select
          className="filter-input"
          value={pendingFilters.sortBy}
          onChange={(e) =>
            setPendingFilters({ ...pendingFilters, sortBy: e.target.value })
          }
        >
          <option value="">Sort By</option>
          <option value="title">Title (A-Z)</option>
          <option value="priceLowHigh">Price: Low to High</option>
          <option value="priceHighLow">Price: High to Low</option>
          <option value="availability">Availability</option>
        </select>

        {/* Price Slider */}
        <div className="price-slider">
          <span>₹{pendingFilters.priceRange[0]}</span>
          <input
            type="range"
            min="0"
            max="1000"
            step="50"
            value={pendingFilters.priceRange[0]}
            onChange={(e) =>
              setPendingFilters({
                ...pendingFilters,
                priceRange: [parseInt(e.target.value), pendingFilters.priceRange[1]]
              })
            }
          />
          <input
            type="range"
            min="0"
            max="1000"
            step="50"
            value={pendingFilters.priceRange[1]}
            onChange={(e) =>
              setPendingFilters({
                ...pendingFilters,
                priceRange: [pendingFilters.priceRange[0], parseInt(e.target.value)]
              })
            }
          />
          <span>₹{pendingFilters.priceRange[1]}</span>
        </div>

        <button className="apply-button" onClick={applyFilters}>
          ✅ Apply Filters
        </button>
      </div>

      {/* Active Filters */}
      <div className="active-filters">
        {filters.genre && (
          <span className="filter-tag">
            Genre: {filters.genre}{" "}
            <X size={14} onClick={() => removeFilter("genre")} />
          </span>
        )}
        {filters.availability && (
          <span className="filter-tag">
            {filters.availability === "true" ? "Available" : "Borrowed"}{" "}
            <X size={14} onClick={() => removeFilter("availability")} />
          </span>
        )}
        {(filters.priceRange[0] !== 0 || filters.priceRange[1] !== 1000) && (
          <span className="filter-tag">
            Price: ₹{filters.priceRange[0]}–₹{filters.priceRange[1]}{" "}
            <X size={14} onClick={() => removeFilter("price")} />
          </span>
        )}
      </div>

      {/* Cart Info */}
      {totalSelected > 0 && (
        <div className="borrow-info">
          ✅ {totalSelected} book(s) selected to borrow | Total Price: ₹
          {totalPrice.toFixed(2)}
        </div>
      )}

      {/* Book Cards */}
      <ul className="book-list">
        {sortedBooks.map((book) => (
          <li
            key={book.id}
            className={`book-card ${
              selectedBooks.find((b) => b.id === book.id) ? "selected" : ""
            }`}
          >
            <h3>{book.title}</h3>
            <p>Genre: {book.genre}</p>
            <p>Status: {book.available ? "Available" : "Borrowed"}</p>
            <p>Price: ₹{parseFloat(book.price).toFixed(2)}</p>
            <div className="button-row">
              <button className="book-button" onClick={() => onSelectBook(book)}>
                Details
              </button>
              <button className="book-button" onClick={() => handleSelectBook(book)}>
                {selectedBooks.find((b) => b.id === book.id) ? "Remove" : "Select"}
              </button>
              {book.available && (
                <button
                  className="book-button borrow"
                  onClick={() => onBorrow(book)}
                >
                  Borrow
                </button>
              )}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
