// import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
  Legend,
} from "recharts";


export default function StatusCharts({ books }) {
  const availableCount = books.filter((b) => b.available).length;
  const borrowedCount = books.length - availableCount;

  const data = [
    {
      name: "Books",
      Available: availableCount,
      Borrowed: borrowedCount,
    },
  ];

  return (
    <div className="status-chart-container">
      <h2 className="status-chart-title">
        ðŸ“Š Borrowing Status ({books.length} total)
      </h2>

      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={data} margin={{ top: 10, right: 30, left: 0, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis allowDecimals={false} />
          <Tooltip />
          <Legend />
          <Bar dataKey="Available" fill="#22c55e" barSize={60} />
          <Bar dataKey="Borrowed" fill="#ef4444" barSize={60} />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}
