import { useState } from "react";

export default function AdminPanel({ onBookAdded, onBookDeleted }) {
  const [newBook, setNewBook] = useState({
    id: "",
    title: "",
    genre: "",
    price: "",
    available: true,
  });

  const handleAdd = async () => {
    if (!newBook.id || !newBook.title || !newBook.genre || !newBook.price) {
      alert("Please fill all fields");
      return;
    }

    try {
      const response = await fetch("http://127.0.0.1:5000/api/books", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...newBook,
          price: parseFloat(newBook.price),
          available: newBook.available === "true" || newBook.available === true
        }),
      });

      if (response.ok) {
        const addedBook = await response.json();
        onBookAdded(addedBook);
        alert("Book added successfully");
        setNewBook({ id: "", title: "", genre: "", price: "", available: true });
      } else {
        alert("Failed to add book");
      }
    } catch (err) {
      console.error(err);
      alert("Error adding book");
    }
  };

  const handleDelete = async () => {
    if (!newBook.id) {
      alert("Enter the ID of the book to delete");
      return;
    }

    try {
      const response = await fetch(`http://127.0.0.1:5000/api/books/${newBook.id}`, {
        method: "DELETE",
      });

      if (response.ok) {
        onBookDeleted(newBook.id);
        alert("Book deleted successfully");
        setNewBook({ ...newBook, id: "" });
      } else {
        alert("Failed to delete book");
      }
    } catch (err) {
      console.error(err);
      alert("Error deleting book");
    }
  };

  return (
    <div className="admin-panel">
      <h2>Admin Panel</h2>
      <input
        type="text"
        placeholder="ID"
        value={newBook.id}
        onChange={(e) => setNewBook({ ...newBook, id: e.target.value })}
      />
      <input
        type="text"
        placeholder="Title"
        value={newBook.title}
        onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
      />
      <input
        type="text"
        placeholder="Genre"
        value={newBook.genre}
        onChange={(e) => setNewBook({ ...newBook, genre: e.target.value })}
      />
      <input
        type="number"
        placeholder="Price"
        value={newBook.price}
        onChange={(e) => setNewBook({ ...newBook, price: e.target.value })}
      />
      <select
        value={newBook.available}
        onChange={(e) =>
          setNewBook({
            ...newBook,
            available: e.target.value === "true" ? true : false,
          })
        }
      >
        <option value="true">Available</option>
        <option value="false">Borrowed</option>
      </select>
      <div style={{ marginTop: "8px" }}>
        <button className="admin-button" onClick={handleAdd}>
          Add Book
        </button>
        <button className="admin-button" onClick={handleDelete}>
          Delete Book by ID
        </button>
      </div>
    </div>
  );
}
