import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
  Legend,
  Cell,
} from "recharts";

export default function StatusChart({ books }) {
  const availableCount = books.filter((book) => book.available === true).length;
  const borrowedCount = books.filter((book) => book.available === false).length;

  const data = [
    { name: "Available ðŸ“—", count: availableCount, color: "#4CAF50" }, // Green
    { name: "Borrowed ðŸ“•", count: borrowedCount, color: "#EF4444" },   // Red
  ];

  return (
    <div className="status-chart-container">
      <h2 className="status-chart-title">ðŸ“Š Borrowed Status Distribution</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={data}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis allowDecimals={false} />
          <Tooltip />
          <Legend />
          <Bar dataKey="count" name="Book Count">
            {data.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.color} />
            ))}
          </Bar>
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}
