import { useEffect, useState } from "react";
import { X } from "lucide-react";

export default function BookList({ onBorrow, onSelectBook }) {
  const [books, setBooks] = useState([]);
  const [search, setSearch] = useState("");
  const [genre, setGenre] = useState("");
  const [availability, setAvailability] = useState("");
  const [sortBy, setSortBy] = useState("");
  const [priceRange, setPriceRange] = useState([0, 1000]);
  const [selectedBooks, setSelectedBooks] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/books")
      .then((res) => res.json())
      .then(setBooks);
  }, []);

  const handleSelectBook = (book) => {
    if (selectedBooks.find((b) => b.id === book.id)) {
      setSelectedBooks(selectedBooks.filter((b) => b.id !== book.id));
    } else {
      setSelectedBooks([...selectedBooks, book]);
    }
  };

  const removeFilter = (type) => {
    if (type === "genre") setGenre("");
    if (type === "availability") setAvailability("");
    if (type === "price") setPriceRange([0, 1000]);
  };

  const filteredBooks = books
    .filter((book) =>
      book.title.toLowerCase().includes(search.toLowerCase())
    )
    .filter((book) => (genre ? book.genre === genre : true))
    .filter((book) =>
      availability === "true"
        ? book.available === true
        : availability === "false"
        ? book.available === false
        : true
    )
    .filter((book) => book.price >= priceRange[0] && book.price <= priceRange[1]);

  const sortedBooks = [...filteredBooks].sort((a, b) => {
    if (sortBy === "title") return a.title.localeCompare(b.title);
    if (sortBy === "priceLowHigh") return a.price - b.price;
    if (sortBy === "priceHighLow") return b.price - a.price;
    if (sortBy === "availability") return b.available - a.available;
    return 0;
  });

  const totalSelected = selectedBooks.length;
  const totalPrice = selectedBooks.reduce((sum, b) => sum + b.price, 0);

  return (
    <div>
      <div className="filter-section">
        <input
          className="filter-input"
          placeholder="Search by title..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <select className="filter-input" value={genre} onChange={(e) => setGenre(e.target.value)}>
          <option value="">All Genres</option>
          <option value="Fiction">Fiction</option>
          <option value="Science">Science</option>
          <option value="Finance">Finance</option>
          <option value="Self-help">Self-help</option>
          <option value="Philosophy">Philosophy</option>
          <option value="Biography">Biography</option>
          <option value="Fantasy">Fantasy</option>
          <option value="Romance">Romance</option>
          <option value="Business">Business</option>
          <option value="History">History</option>
        </select>
        <select
          className="filter-input"
          value={availability}
          onChange={(e) => setAvailability(e.target.value)}
        >
          <option value="">All</option>
          <option value="true">Available</option>
          <option value="false">Borrowed</option>
        </select>
        <select className="filter-input" value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="">Sort By</option>
          <option value="title">Title (A-Z)</option>
          <option value="priceLowHigh">Price: Low to High</option>
          <option value="priceHighLow">Price: High to Low</option>
          <option value="availability">Availability</option>
        </select>
      </div>

      {/* Active Filters */}
      <div className="active-filters">
        {genre && (
          <span className="filter-tag">
            Genre: {genre} <X size={14} onClick={() => removeFilter("genre")} />
          </span>
        )}
        {availability && (
          <span className="filter-tag">
            {availability === "true" ? "Available" : "Borrowed"} <X size={14} onClick={() => removeFilter("availability")} />
          </span>
        )}
        {(priceRange[0] !== 0 || priceRange[1] !== 1000) && (
          <span className="filter-tag">
            Price: ₹{priceRange[0]}–₹{priceRange[1]} <X size={14} onClick={() => removeFilter("price")} />
          </span>
        )}
      </div>

      {/* Price Range Sliders */}
      <div className="price-slider">
        <span>₹{priceRange[0]}</span>
        <input
          type="range"
          min="0"
          max="1000"
          step="50"
          value={priceRange[0]}
          onChange={(e) => setPriceRange([parseInt(e.target.value), priceRange[1]])}
        />
        <input
          type="range"
          min="0"
          max="1000"
          step="50"
          value={priceRange[1]}
          onChange={(e) => setPriceRange([priceRange[0], parseInt(e.target.value)])}
        />
        <span>₹{priceRange[1]}</span>
      </div>

      {/* Cart Info */}
      {totalSelected > 0 && (
        <div className="borrow-info">
          ✅ {totalSelected} book(s) selected to borrow | Total Price: ₹{totalPrice}
        </div>
      )}

      {/* Book Cards */}
      <ul className="book-list">
        {sortedBooks.map((book) => (
          <li
            key={book.id}
            className={`book-card ${selectedBooks.find((b) => b.id === book.id) ? "selected" : ""}`}
          >
            <h3>{book.title}</h3>
            <p>Genre: {book.genre}</p>
            <p>Status: {book.available ? "TRUE" : "FALSE"}</p>
             <p>Price: ₹{Number(book.price).toFixed(2)}</p>
            <div className="button-row">
              <button className="book-button" onClick={() => onSelectBook(book)}>Details</button>
              <button className="book-button" onClick={() => handleSelectBook(book)}>
                {selectedBooks.find((b) => b.id === book.id) ? "Remove" : "Select"}
              </button>
              {book.available && (
                <button className="book-button borrow" onClick={() => onBorrow(book)}>Borrow</button>
              )}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
