// import React
import { useEffect, useState } from "react";
import BookList from "./components/BookList";
import BorrowedBooks from "./components/BorrowedBooks";
import BookDetailModal from "./components/BookDetailModal";
import GenrePieChart from "./components/GenrePieChart";
import StatusChart from "./components/StatusChart";

export default function App() {
  const [books, setBooks] = useState([]);
  const [borrowed, setBorrowed] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);
  const [showAnalytics, setShowAnalytics] = useState(false);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/books")
      .then((res) => res.json())
      .then((data) => setBooks(data));
  }, []);

  const handleBorrow = (book) => {
    fetch(`http://127.0.0.1:5000/api/borrow/${book.id}`, { method: "POST" })
      .then((res) => {
        if (res.ok) {
          setBorrowed((prev) => [...prev, book]);
          setBooks((prev) =>
            prev.map((b) =>
              b.id === book.id ? { ...b, available: false } : b
            )
          );
        }
      })
      .catch((err) => console.error("Borrow failed:", err));
  };

  const handleReturn = (book) => {
    fetch(`http://127.0.0.1:5000/api/return/${book.id}`, { method: "POST" })
      .then((res) => {
        if (res.ok) {
          setBorrowed((prev) => prev.filter((b) => b.id !== book.id));
          setBooks((prev) =>
            prev.map((b) =>
              b.id === book.id ? { ...b, available: true } : b
            )
          );
        }
      })
      .catch((err) => console.error("Return failed:", err));
  };

  return (
    <div className="min-h-screen p-6 bg-gradient-to-br from-blue-50 to-indigo-100 text-gray-800">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">ðŸ“š Library Book Explorer</h1>
        <button
          onClick={() => setShowAnalytics((prev) => !prev)}
          className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
        >
          {showAnalytics ? "ðŸ“– Back to Books" : "ðŸ“Š View Analytics"}
        </button>
      </div>

      {showAnalytics ? (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 justify-items-center">
          <GenrePieChart books={books} />
          <StatusChart books={books} />
        </div>
      ) : (
        <>
          <BookList
            books={books}
            onBorrow={handleBorrow}
            onSelectBook={setSelectedBook}
          />
          <div className="my-10">
            <BorrowedBooks borrowed={borrowed} onReturn={handleReturn} />
          </div>
        </>
      )}

      {selectedBook && (
        <BookDetailModal
          book={selectedBook}
          onClose={() => setSelectedBook(null)}
        />
      )}
    </div>
  );
}
