import { useState } from "react";
import BookList from "./components/BookList";
import BorrowedBooks from "./components/BorrowedBooks";
import BookDetailModal from "./components/BookDetailModal";
import GenrePieChart from "./components/GenrePieChart";
import StatusChart from "./components/StatusChart";
import "./index.css";

function App() {
  const [borrowed, setBorrowed] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);
  const [view, setView] = useState("list"); // 'list' | 'borrowed' | 'analytics'
  const [analyticsType, setAnalyticsType] = useState(""); // 'genre' | 'status'
  const [showDropdown, setShowDropdown] = useState(false);

  const handleBorrow = (book) => {
    fetch(`http://127.0.0.1:5000/api/borrow/${book.id}`, {
      method: "POST",
    }).then(() => {
      setBorrowed((prev) => [...prev, { ...book, available: false }]);
    });
  };

  const handleReturn = (book) => {
    fetch(`http://127.0.0.1:5000/api/return/${book.id}`, {
      method: "POST",
    }).then(() => {
      setBorrowed((prev) => prev.filter((b) => b.id !== book.id));
    });
  };

  const btnStyle = {
    padding: "6px 12px",
    borderRadius: "6px",
    cursor: "pointer",
    border: "1px solid #ccc",
    backgroundColor: "#fff",
  };

  const dropdownBtn = {
    display: "block",
    padding: "6px 12px",
    width: "100%",
    background: "#fff",
    border: "none",
    cursor: "pointer",
    textAlign: "left",
  };

  return (
    <div className="App" style={{ padding: "20px", backgroundColor: "#f7f7fb" }}>
      <h1 style={{ fontSize: "2rem", fontWeight: "bold", marginBottom: "10px" }}>
        ðŸ“š Library Book Explorer
      </h1>

      {/* Navigation Buttons */}
      <div style={{ display: "flex", gap: "10px", marginBottom: "20px", position: "relative" }}>
        <button onClick={() => setView("list")} style={btnStyle}>ðŸ“– Book List</button>
        <button onClick={() => setView("borrowed")} style={btnStyle}>ðŸ“˜ Borrowed Books</button>

        <button style={btnStyle} onClick={() => setShowDropdown(!showDropdown)}>
          ðŸ“Š Analytics â–¾
        </button>

        {showDropdown && (
          <div style={{
            position: "absolute",
            top: "100%",
            left: "220px",
            backgroundColor: "#fff",
            border: "1px solid #ccc",
            borderRadius: "4px",
            zIndex: 10
          }}>
            <button
              style={dropdownBtn}
              onClick={() => {
                setView("analytics");
                setAnalyticsType("genre");
                setShowDropdown(false);
              }}
            >
              ðŸ“ˆ Genre Distribution
            </button>
            <button
              style={dropdownBtn}
              onClick={() => {
                setView("analytics");
                setAnalyticsType("status");
                setShowDropdown(false);
              }}
            >
              ðŸ“Š Borrowed Status
            </button>
          </div>
        )}
      </div>

      {/* Content Section */}
      {view === "list" && (
        <>
          <BookList onBorrow={handleBorrow} onSelectBook={setSelectedBook} />
        </>
      )}

      {view === "borrowed" && (
        <>
          <BorrowedBooks borrowed={borrowed} onReturn={handleReturn} />
        </>
      )}

      {view === "analytics" && (
        <>
          {analyticsType === "genre" && <GenrePieChart books={borrowed} />}
          {analyticsType === "status" && <StatusChart books={borrowed} />}
        </>
      )}

      {/* Book Detail Modal */}
      <BookDetailModal book={selectedBook} onClose={() => setSelectedBook(null)} />
    </div>
  );
}

export default App;
