// import React from "react";
import {
  PieChart,
  Pie,
  Cell,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";


const COLORS = [
  "#6366f1", // Indigo
  "#10b981", // Emerald
  "#f59e0b", // Amber
  "#ef4444", // Red
  "#3b82f6", // Blue
  "#a855f7", // Purple
  "#14b8a6", // Teal
];

export default function GenrePieChart({ books }) {
  const genreCount = {};
  books.forEach((book) => {
    genreCount[book.genre] = (genreCount[book.genre] || 0) + 1;
  });

  const data = Object.entries(genreCount).map(([name, value]) => ({
    name,
    value,
  }));

  return (
    <div className="chart-container">
      <h2 className="chart-title">
        ðŸ“ˆ Genre Distribution ({data.length} genres)
      </h2>

      <ResponsiveContainer width="100%" height={300}>
        <PieChart>
          <Pie
            data={data}
            cx="50%"
            cy="50%"
            outerRadius={100}
            label
            dataKey="value"
          >
            {data.map((entry, index) => (
              <Cell key={index} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </ResponsiveContainer>
    </div>
  );
}
