import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
  Legend,
} from "recharts";

export default function StatusChart({ books }) {
  // Count books
  const availableCount = books.filter((book) => book.available === true).length;
  const borrowedCount = books.filter((book) => book.available === false).length;

  const data = [
    { name: "Available ðŸ“—", count: availableCount,color: "#4CAF50" },
    { name: "Borrowed ðŸ“•", count: borrowedCount,color: "#EF4444" },
  ];

  return (
    <div className="status-chart-container">
      <h2 className="status-chart-title">ðŸ“Š Borrowed Status Distribution</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={data}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis allowDecimals={false} />
          <Tooltip />
          <Legend />
          <Bar dataKey="count" fill="#4CAF50" name="Books Status" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}
