import { X, Star } from "lucide-react";

export default function BookDetailModal({ book, onClose }) {
   const [coverUrl, setCoverUrl] = useState(null);

  useEffect(() => {
    if (book?.title) {
      fetch(
        `https://www.googleapis.com/books/v1/volumes?q=intitle:${encodeURIComponent(book.title)}${
          book.author ? `+inauthor:${encodeURIComponent(book.author)}` : ""
        }`
      )
        .then((res) => res.json())
        .then((data) => {
          const image =
            data.items?.[0]?.volumeInfo?.imageLinks?.thumbnail?.replace("http:", "https:") || null;
          setCoverUrl(image);
        })
        .catch((err) => {
          console.error("Image fetch failed:", err);
        });
    }
  }, [book]);

  if (!book) return null;

  return (
    <div className="modal-backdrop">
      <div className="modal-content">
        <button className="modal-close-btn" onClick={onClose}>
          <X size={22} />
        </button>
       

        <h2 className="modal-title">{book.title}</h2>
        <div className="modal-info">
          <p><strong>Author:</strong> {book.author || "Unknown"}</p>
          <p><strong>Genre:</strong> {book.genre}</p>
          <p><strong>Publish Date:</strong> {book.publish_date || "N/A"}</p>
          <p>
            <strong>Status:</strong>{" "}
            <span className={book.available ? "status-available" : "status-borrowed"}>
              {book.available ? "Available" : "Borrowed"}
            </span>
          </p>
          <p><strong>Price:</strong> â‚¹{parseFloat(book.price).toFixed(2)}</p>
          <p><strong>Rating:</strong>{" "}
            <span className="rating">
              {[...Array(Math.round(book.rating || 4))].map((_, i) => (
                <Star key={i} size={16} fill="#facc15" color="#facc15" />
              ))}
              <span className="rating-value">({book.rating || 4}.0)</span>
            </span>
          </p>
        </div>

        <button className="modal-action-btn" onClick={onClose}>
          Close
        </button>
      </div>
    </div>
  );
}
