// StatusChart.js
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Legend } from 'recharts';

export default function StatusChart({ books }) {
  const statusData = [
    {
      name: 'Books',
      Available: books.filter(book => book.available).length,
      Borrowed: books.filter(book => !book.available).length,
    },
  ];

  return (
    <div style={{ width: '100%', height: 300, marginTop: '40px' }}>
      <h2 style={{ textAlign: 'center' }}>ðŸ“Š Borrowed vs Available Status</h2>
      <ResponsiveContainer>
        <BarChart data={statusData}>
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="Available" fill="#4caf50" />
          <Bar dataKey="Borrowed" fill="#f44336" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}
